class execute():

    def __init__(self, token_to_buy, web3):
        self.web3 = web3
        self.token_to_buy = self.web3.toChecksumAddress(token_to_buy)
        self.pancake_contract = web3.eth.contract(address=pancake_router, abi=pancake_abi)
        self.token_contract = web3.eth.contract(address=token_to_buy, abi=token_abi)
        self.nonce = web3.eth.get_transaction_count(wallet_address)
        self.bnb_address = web3.toChecksumAddress(wbnb)

    def token_info(self):
        token_name = self.token_contract.functions.name().call()
        token_symbol = self.token_contract.functions.symbol().call()
        return token_name, token_symbol

    def buy(self, amount=0.0):
        try:
            if self.token_to_buy:
                # wbnb contract address
                buy_txn = self.pancake_contract.functions.swapExactETHForTokens(
                    0,
                    [self.bnb_address, self.token_to_buy],
                    wallet_address,
                    (int(time.time()) + revert_time)
                ).buildTransaction({
                    'from': wallet_address,
                    'value': self.web3.toWei(float(amount or spend_amount), 'ether'),
                    # This is the Token(BNB) amount you want to Swap from
                    'gas': gas_amount,
                    'gasPrice': self.web3.toWei(gas_price, 'gwei'),
                    'nonce': self.nonce,
                })
                tx_token = None
                try:
                    self.send_transation(txn=buy_txn, msg='Buy')
                except Exception as error:
                    print(style.RED + currentTimeStamp + " Transaction failed.")
                    print("Error: %s" %error)  # line break: move onto scanning for next token
                if tx_token:
                    txHash = str(self.web3.toHex(tx_token))

                    # TOKEN IS BOUGHT
                    check_success_url = "https://api.bscscan.com/api?module=transaction&action=gettxreceiptstatus&txhash=" \
                                                 + txHash + "&apikey=" + bscscan_api
                    response_result = requests.get(url=check_success_url)
                    token_name, token_symbol = self.token_info()
                    if response_result.json()['status'] == "1":

                        print(style.GREEN + currentTimeStamp + " [BUY] Successfully bought $" + token_name +
                              "(" + token_symbol +")" + " for " + style.BLUE + str(spend_amount) + style.GREEN + " BNB - TX ID: ", txHash)
                    else:
                        print(style.RED + currentTimeStamp + " [BUY] Transaction failed: likely not enough gas.")

        except Exception as error:
            print(style.RED + currentTimeStamp + " [ERROR] Unknown error with buying token: " + str(error))

    def approve(self):
        balance = self.token_contract.functions.balanceOf(wallet_address).call()
        approve_txn = self.token_contract.functions.approve(pancake_router, balance).buildTransaction({
            'from': wallet_address,
            'gasPrice': self.web3.toWei('10', 'gwei'),
            'nonce': self.nonce,
        })
        self.send_transation(txn=approve_txn, msg='Approve')

    def sell(self):
        sell_txn = self.pancake_contract.functions.swapExactTokensForETH(
            tokenValue, 0,
            [self.token_to_buy, self.bnb_address],
            wallet_address,
            (int(time.time()) + revert_time)

        ).buildTransaction({
            'from': wallet_address,
            'gasPrice': self.web3.toWei('10', 'gwei'),
            'nonce': self.nonce
        })
        self.send_transation(txn=sell_txn, msg='Sell')

    def send_transation(self, txn, msg):
        signed_txn = self.web3.eth.account.sign_transaction(txn, private_key=wallet_privateKey)
        tx_token = self.web3.eth.send_raw_transaction(signed_txn.rawTransaction)
        print(f"{style.GREEN} {currentTimeStamp} {msg} transaction sent\n{self.web3.toHex(tx_token)}")

    def get_price(self):
        moralis_price_url = f'https://deep-index.moralis.io/api/v2/erc20/{self.token_to_buy}/price?chain=bsc'
        sell = False
        while True:
            try:
                response = requests.request("GET", moralis_price_url, headers=moralis_headers)
                result = response.json()
                price_bnb = self.web3.fromWei(int(result['nativePrice']['value']), 'ether')
                price_usdt = result['usdPrice']
                print(f"BNB: {price_bnb} | USDT: {price_usdt}")
            except Exception as error:
                print(error)
            time.sleep(2)
